import './App.css';
import React, {useState, useEffect} from 'react';
import { useParams } from "react-router-dom";
import FlashCard from './FlashCard';
import {FlashCardSets} from './Data/FlashCardSets';

function FlashCards() {
    let { id } = useParams();
    let deck = FlashCardSets[id];
    const [activeCardId, setActiveCardId] = useState("0");
    let lastId = deck.length;

    function prevCard() {
    }

    function nextCard() {
        if(activeCardId<lastId){
            document.getElementById(activeCardId+1).style={'visibility': 'hidden'}
            activeCardId++;
        }
    }

    return(
        <div className="flexbox-row">
            {/*
            <div id="activeCard">
                <FlashCard card={deck[0]}/>
            </div>
            */}
            {deck.map(card => {
                return (
                    <div id={card} style={{'visibility': 'visible'}}>
                        <FlashCard card={card}/>
                    </div>
                );
            })}
            <button onClick={() => prevCard()}>Prev</button>
            <button onClick={() => nextCard()}>Next</button>
        </div>
    );
}

export default FlashCards;
