import './App.css';
import React, {useState, useEffect} from 'react';
import { useParams } from "react-router-dom";
import FlashCard from './FlashCard';
import {FlashCardSets} from './Data/FlashCardSets';

function FlashCards() {
    let { id } = useParams();
    let deck = FlashCardSets[id];
    const [activeCardId, setActiveCardId] = useState(1);
    let lastId = deck.length;

    const prevCard = () => {
    }

    const nextCard = () => {
        if(activeCardId<lastId){
            document.getElementById(activeCardId+1).style="visibility: visible"
            setActiveCardId(activeCardId+1);
            //setActiveCardId(prevState => ({
            //    activeCardId: prevState.activeCardId+1
            //}));
            console.log(activeCardId);
        }
    }

    //useEffect(
        //console.log("hi")
        //document.getElementById(1).style="visibility: visible"
    //);

    return(
        <div className="flexbox-row">
            {/*
            <div id="activeCard">
                <FlashCard card={deck[0]}/>
            </div>
            */}
            {deck.map(card => {
                return (
                    <div id={card} style={{'visibility': 'hidden'}}>
                        <FlashCard card={card}/>
                    </div>
                );
            })}
            {/*document.getElementById(1).style="visibility: visible"*/}
            <button onClick={prevCard}>Prev</button>
            <button onClick={nextCard}>Next</button>
            {activeCardId}
        </div>
    );
}

export default FlashCards;
