import './App.css';
import React, {useState, useEffect} from 'react';
import { useParams } from "react-router-dom";
import FlashCard from './FlashCard';
import {FlashCardSets} from './Data/FlashCardSets';

function FlashCards() {
    let { id } = useParams();
    let deck = FlashCardSets[id];
    let activeCardId = 0;
    let lastId = deck.length;

    function prevCard() {
        if(activeCardId-1 > 0){
            document.getElementById("activeCard").innerHTML = () => {
            <FlashCard card={deck[activeCardId-1]}/>}
            activeCardId--;
        }
    }

    function nextCard() {
        if(activeCardId < lastId){
            document.getElementById("activeCard").innerHTML = () => {
            <FlashCard card={deck[activeCardId+1]}/>}
            activeCardId++;
        }
    }

    return(
        <div>
            <div id="activeCard">
                <FlashCard card={deck[0]}/>
            </div>
            <button onClick={() => prevCard()}>Prev</button>
            <button onClick={() => nextCard()}>Next</button>
            {lastId}
            {/*deck.map(card => {
                return (
                    <FlashCard card={card}/>
                );
            })*/}
        </div>
    );
}

export default FlashCards;
