import './App.css';
import React, {useState, useEffect} from 'react';
import { useParams } from "react-router-dom";
import FlashCard from './FlashCard';
import {FlashCardSets} from './Data/FlashCardSets';

function FlashCards() {
    let { id } = useParams();
    let deck = FlashCardSets[id];
    let activeCardId = 0;
    let lastId = deck.length;

    return(
        <div>
            <div id="activeCard">
                <FlashCard card={deck[activeCardId]}/>
            </div>
            <button>Prev</button>
            <button>Next</button>
            {lastId}
            {/*deck.map(card => {
                return (
                    <FlashCard card={card}/>
                );
            })*/}
        </div>
    );
}

export default FlashCards;
