import './App.css';
import React, {useState, useEffect} from 'react';
import { FlashCardData } from './Data/FlashCardData';

function FlashCards(props) {
    let id = props.card;
    let data = FlashCardData[id];
    let front = data.front;
    let back = data.back;
    let state = "front";

    function flash() {
        if (document.getElementById("front").style.visibility != "hidden") {
            document.getElementById("front").style.visibility = "hidden";
            document.getElementById("back").style.visibility = "visible";
        } else {
            document.getElementById("front").style.visibility = "visible";
            document.getElementById("back").style.visibility = "hidden";
        }
    }

    function flip() {
        if (state == "front"){
            document.getElementById("content").innerHTML = back;
        }
        else if (state == "back"){
            document.getElementById("content").innerHTML = front;
        }
    }

    return(
        <div className="flash-card">
            <div id="content">
                {front}
            </div>
            <div id="front">
                {front}
            </div>
            <div id="back" style={{visibility: "hidden"}}>
                {back}
            </div>
            <button onClick={() => flip()}>Flip</button>
        </div>
    );
}

export default FlashCards;
